# frozen_string_literal: true

class CreateTenant
  def create_tenant(subscription)
    tenant_name = subscription.tenant_name
    Apartment::Tenant.create(tenant_name)
    tenant = Tenant.create(name: tenant_name, initial_plan_id: subscription.subscription_plan_id)
    subscription.tenant = tenant
    subscription.save
    tenant.price = { bigCommerce_feature: { toggle: false, amount: 30, stripe_id: '' }, shopify_feature: { toggle: false, amount: 30, stripe_id: '' }, magento2_feature: { toggle: false, amount: 30, stripe_id: '' }, teapplix_feature: { toggle: false, amount: 30, stripe_id: '' }, product_activity_log_feature: { toggle: false, amount: 30, stripe_id: '' }, magento_soap_feature: { toggle: false, amount: 30, stripe_id: '' }, multi_box_feature: { toggle: false, amount: 30, stripe_id: '' }, amazon_fba_feature: { toggle: false, amount: 30, stripe_id: '' }, post_scanning_feature: { toggle: false, amount: 30, stripe_id: '' }, allow_Real_time_feature: { toggle: false, amount: 30, stripe_id: '' }, import_option_feature: { toggle: false, amount: 30, stripe_id: '' }, inventory_report_option_feature: { toggle: false, amount: 30, stripe_id: '' }, custom_product_fields_feature: { toggle: false, amount: 30, stripe_id: '' }, high_sku_feature: { toggle: false, amount: 50, stripe_id: '' }, double_high_sku: { toggle: false, amount: 100, stripe_id: '' }, cust_maintenance_1: { toggle: false, amount: 30, stripe_id: '' }, cust_maintenance_2: { toggle: false, amount: 30, stripe_id: '' }, groovelytic_stat_feature: { toggle: false, amount: 30, stripe_id: '' }, product_ftp_import: { toggle: false, amount: 30, stripe_id: '' } }
    tenant.save
    Apartment::Tenant.switch!(tenant_name)
    apply_restrictions_and_seed(subscription)
    delay(run_at: 1.seconds.from_now, queue: 'create_tenant_at_groovelytics', priority: 95).create_groovelytics_tenant(tenant_name)
    # self.create_groovelytics_tenant(tenant_name)
    create_default_csv_map

    subscription.update_progress('tenant_created')
    delay(run_at: 20.minutes.from_now, queue: 'send_transaction_emails', priority: 95).send_transaction_emails(subscription)
    create_csv_store
    # TransactionEmail.delay(run_at: 2.hours.from_now).send_email(subscription)
    # TransactionEmail.welcome_email(subscription).deliver
    # subscription.update_progress("email_sent")
  end

  def create_csv_store
    store = Store.new
    store.name = 'CSV'
    store.store_type = 'CSV'
    store.csv_beta = true
    store.status = true
    store.inventory_warehouse_id = InventoryWarehouse.first.id
    store.save!
  end

  def apply_restrictions_and_seed(subscription)
    ApplyAccessRestrictions.new.delay(
      run_at: 10.minutes.from_now,
      queue: "apply_access_restrictions_#{subscription.tenant_name}",
      priority: 95
    ).apply_access_restrictions(subscription.tenant_name)
    Groovepacker::SeedTenant.new.seed(true,
                                      subscription.user_name,
                                      subscription.email,
                                      subscription.password)
    setting = GeneralSetting.all.first
    setting.email_address_for_packer_notes = subscription.email
    setting.email_address_for_billing_notification = subscription.email
    setting.save
  end

  def send_transaction_emails(subscription)
    if subscription.reload.status == 'completed'
      TransactionEmail.send_email(subscription).deliver
    else
      TransactionEmail.failed_subscription(subscription, nil).deliver
    end
    # TransactionEmail.welcome_email(subscription).deliver
  end

  def create_groovelytics_tenant(tenant_name)
    HTTParty::Basement.default_options.update(verify: false)
    HTTParty.post(
      "#{ENV['GROOV_ANALYTIC_URL']}/tenants",
      headers: { 'Content-Type' => 'application/json', 'tenant' => tenant_name }
    )
    send_user_info_obj = SendUsersInfo.new
    send_user_info_obj.build_send_users_stream(tenant_name)
  rescue => e
    Rails.logger.error e.backtrace.join("\n")
  ensure
    HTTParty::Basement.default_options.update(verify: true)
  end

